UPDATE `locales_quest` SET 
	`Details_loc8`='Мой брат Квинн был тяжело ранен вурдалаком, Иваром Нечистым, и я не знаю, сможет ли он как следует поправиться без помощи магии. Хотя я сама и не фармацевт, но знаю, что наши аптекари могут изготавливать исцеляющие зелья из обесцвеченных сердец воргов.$B$BСоберите такие сердца и отнесите их аптекарю Ренферрелу к Гробнице, на юг отсюда. А потом возвращайтесь ко мне с зельем.$B$BМножество воргов водится между этим местом и садом Мальдена к востоку отсюда.' 
WHERE `entry` = 429;